import { useState, createContext, useMemo } from "react";

export const AppContext = createContext();

export default function AppProVider({ children }) {
   const [mainVideo, setMainVideo] = useState("Hello Word!");

   const updateMainVideo = (value) => {
      const update = useMemo(() => {
         setMainVideo(value);
      }, [value]);
   };

   return (
      <AppContext.Provider value={{ mainVideo, updateMainVideo }}>
         {children}
      </AppContext.Provider>
   );
}
