export default function videoReducer({ state = [] }, action) {
   const data = action.payload;
   switch (action.type) {
      case "ADD_VIDEO":
         if (state) {
            const checkExist = state.find(
               (video) => video.videoId === data.videoId
            );
         }

         if (!checkExist) {
            return [...state, data];
         }
         const state_temp = [...state, data];
         const found = state_temp.find((video) => video.channel === "Masew");
         return found;

      //  return [...state, data];
      default:
         return;
   }
}
