import { useState, createContext, useMemo } from "react";

export const AppContext = createContext();

export default function AppProvider({ children }) {
   const { mainVideo, setMainVideo } = useContext(AppContext);

   return (
      <AppContext.Provider value={{ mainVideo, setMainVideo }}>
         {children}
      </AppContext.Provider>
   );
}
