export default function videoReducer(state, action) {
   const data = action.payload;
   switch (action.type) {
      case "ADD_VIDEO":
         return state.find((video) => {
            if (video.videoId === data.videoId) {
               return "video exist";
            } else if (data.duration > 600) {
               return "video too long";
            } else {
               return [...state, data];
