import Box from '@material-ui/core/Box';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import ButtonBase from '@material-ui/core/ButtonBase';
import Typography from '@material-ui/core/Typography';
import {useState, useEffect} from 'react';
import Fade from '@material-ui/core/Fade';     
import { makeStyles } from '@material-ui/core/styles';
import theme from '../Theme';
import { ThemeProvider } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Collapse from '@material-ui/core/Collapse';

    const useStyles = makeStyles((theme) => ({
        root: {
          display: 'flex',
          '& .MuiButtonBase-root': {
              justifyContent: "flex-start"
          },
          justifyContent: "flex-start"
        },
        details: {
          display: 'flex',
          flexDirection: 'column',
        },
        content: {
          flex: '1 0 auto',
        },
        cover: {
            width: "30%",
            height: '100%'
        },
        controls: {
          display: 'flex',
          alignItems: 'center',
          paddingLeft: theme.spacing(1),
        //   paddingBottom: theme.spacing(1),
        },
        media:{
          padding: 0,
          paddingLeft: '15px',
          paddingBottom: '10px !important',
          paddingRight: "15px"
        },
        desc: {
          fontSize: '10px',
          marginTop: '15px',
          textAlign: 'initial'
        },
        title: {
          fontSize: '15px',
          textAlign: 'initial'
        },
        centerRipple: true

    }));



const ResultVideos = ({ searchResults, res }) => {
        const classes = useStyles();
        const [collapse,setCollapse] = useState(false);

        useEffect(() => {

        }, [])

        const secondsToHms = (d) => {
            d = Number(d);
            var h = Math.floor(d / 3600);
            var m = Math.floor(d % 3600 / 60);
            var s = Math.floor(d % 3600 % 60);
            return `${m}:${(s === 0 ? '00' : +s)}`; 
        } 


        const handleOnClick = (e) => {  
            const value = e.currentTarget.value;
            const videoSelect = searchResults.find((item) => item.videoId === value); 
            res(videoSelect);
        }

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <Box component="div" mt={2} >
            <Box  style={{height: '500px'}} overflow="auto" className={classes.list}>
            {
                searchResults.map((video,idx) => 
                        <Box key={idx} p={1} > 
                            <Card className={classes.root}>
                                <ButtonBase
                                    value={video.videoId}
                                    onClick={handleOnClick}
                                    className={classes.root}
                                >
                                    <CardMedia
                                        className={classes.cover}
                                        component="img"
                                        src={video.thumbnails[0].url}
                                        title="Live from space album cover"
                                    />
                                    <CardContent className={classes.media}>
                                        <Typography className={classes.title} component="h6" variant="h6">
                                            {video.title}
                                        </Typography>
                                        <Typography className={classes.desc} component="p" color="textSecondary">
                                            {video.channel}
                                            <br/>
                                            {video.views}
                                            <br/>
                                            {secondsToHms(video.duration)}
                                        </Typography>
                                    </CardContent>
                                </ButtonBase>
                            </Card>
                        </Box>
                )         
            }
            </Box>

        </Box>
        </ThemeProvider>

    )
}

export default ResultVideos
