export default function videoReducer(state, action) {
   const data = action.payload;
   switch (action.type) {
      case "ADD_VIDEO":
         if (!checkExist) {
            if (action.payload.duration < 600) {
               return [...state, data];
            } else {
               return typeof state;
               //    return "video too long";
            }
         } else {
            // return "video exist";
            return typeof state;
         }

      default:
         return;
   }
}
