import { useState, createContext, useReducer } from "react";
import { videoReducer } from "./AppReducer";

const initialState = [];
export const AppContext = createContext(initialState);

export default function AppProvider({ children }) {
   const [mainVideo, setMainVideo] = useState("Main Video!");
   const [activeVideo, setActiveVideo] = useState("Active Video!");

   return (
      <AppContext.Provider value={{ mainVideo, setMainVideo, activeVideo }}>
         {children}
      </AppContext.Provider>
   );
}
