export default function videoReducer(state = [], action) {
   const data = action.payload;
   switch (action.type) {
      case "ADD_VIDEO":
         if (state) {
            const checkExist = state.find(
               (video) => video.videoId === data.videoId
            );
            if (!checkExist) {
               if (data.duration > 600) {
                  return "video too long";
               }
               return [...state, data];
            } else {
               return "video exist";
            }
         }
         break;
      default:
         return;
   }
}
