export default function videoReducer(state, action) {
   const data = action.payload;
   switch (action.type) {
      case "ADD_VIDEO":
         const checkExist = state.every(data.videoId);
         //  const checkExist = [
         //     ...state,
         //     state.find((video) => video.videoId === data.videoId),
         //  ];
         if (!checkExist) {
            if (action.payload.duration < 600) {
               return [...state, data];
            } else {
               //    return typeof state;
               return "video too long";
            }
         } else {
            return "video exist";
         }

      default:
         return;
   }
}
